import { Component } from '@angular/core';



@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'capcarreer';
}
<header>
  <div style="height: 80px" >
    <img src="../assets/image/Logo.jpg" alt ="Logo" width="140px">
</div>
</header>

<div class="bg-image" style="
      background-image: url('https://media-exp1.licdn.com/dms/image/C4D22AQHMaEuUFEbHnA/feedshare-shrink_2048_1536/0/1663690203546?e=2147483647&v=beta&t=kDio6BwQPnUKsMSw9kwWvC-J7qBJpvjqTtYH02FaSqM');

      width: 100%;
    height: 400px;
    background-size: 100% 100%;">
</div>

<div class="container">
  <div class="row">

    <!--Bluebox-->
    <div class="col-3 align-self-start" style="position: relative; bottom: 1px; right: 200px; ">

        <div class="text-white" style="position: relative; bottom: 185px;  padding-left: 12rem;">
         <div style="background-image: linear-gradient(239deg,#007bbd,#003f61);
         opacity: .9;
         border-radius: 4px;
         box-shadow: 0 25px 100px 0 rgba(0,0,0,.15);
         display: -ms-flexbox;
         display: flex;
         -ms-flex-direction: column;
         flex-direction: column;
         -ms-flex-pack: center;
         justify-content: center;
         -ms-flex-align: start;
         align-items: flex-start;
         height: 350px;
         width: 600px;
         padding: 2rem;
         flex: 0 0 auto;
  ">
   <h1 style="font-size: 280%; font-family: sans-serif;"  class="mb-3">CAPCAREER </h1>
  </div>

        </div>
      </div>
    <!--paragraph-->
    <div class="col-7 align-self-center" style="position: relative; top: 200px;">
  <p>Du bist bereit, deine Expertise für eine nachhaltige und integrative Zukunft einzusetzen und Teil einer globalen und
             diversen Community zu werden? Dann finde jetzt deinen neuen Job bei Capgemini. Get The Future You Want.
            </p>
    </div>

    <!--button-->
    <div class="col-2 align-self-end ">
          <button class="button button4"  title="Finde deinen Job!" type="button" data-bs-toggle="modal" data-bs-target="#staticBackdrop">
             CapCareer ⟶
           </button>
        </div>
    </div>
</div>

<!-- Modal wizzard-->
<div class="modal fade bd-example-modal-lg" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-hidden="true"  aria-labelledby="staticBackdropLabel" aria-hidden="true">
  <div class="modal-dialog modal-lg modal-dialog-centered">
    <app-firstpage></app-firstpage>
  </div>
</div>

<!--footer-->
<footer class="bg-primary text-white text-center text-lg-start fixed-bottom">
  <div class="text-center p-3" style="background-color: rgba(0, 0, 0, 0.2)">
    © 2022 Copyright:
    <a class="text-white" href="https://mdbootstrap.com/">CapCareer.com</a>
  </div>
</footer>
<router-outlet></router-outlet>
